mapper.select:

select APNDNG_FILE_NM apndngFileNm, APNDNG_FILE_SIZE apndngFileSize, STORG_PATH storgPath, SAVE_FILE_NAME saveFileName
from apndng_file
WHERE APNDNG_FILE_NO = #{id};


select APNDNG_FILE_NO apndngFileNo, APNDNG_FILE_NM apndngFileNm, APNDNG_FILE_SIZE apndngFileSize, STORG_PATH storgPath,PRNTS_NO prntsNo, SAVE_FILE_NAME saveFileName
from apndng_file
WHERE PRNTS_NO = #{no}
and PRNTS_DSTNCT=#{type};
