select university, graduate, pno3, major, pno2, lab,pno1,SLCTN_NOTI_NO 'slctnNotiNo',SYMBOL_COLOR_NM 'symbolColorNm',SLCTN_NOTI_DSTNCT 'slctnNotiDstnct', NOTI_REGST_DT 'notiRegstDt',SLCTN_BEGIN_DT 'slctnBeginDt',SLCTN_END_DT 'slctnEndDt',dday,percent,SLCTN_TITLE 'slctnTitle',SLCTN_YYCL 'slctnYycl',SLCTN_SEMSTR 'slctnSemstr',PCHRG_YN 'pchrgYn',BK21_EORGNZ_SUPRT_YN 'bk21EorgnzSuprtYn', STORG_PATH 'storgPath',WRTBT_NO 'wrtbtNo', BBS_NM 'bbsNm',WRTBT_TEXT 'wrtbtText',WRTBT_TITLE 'wrtbtTitle',AVLBL_BEGIN_DT 'avlblBeginDt',WRTBT_DSTNCT 'wrtbtDstnct', CO_STORG_PATH 'coSrorgPath', year(NOTI_REGST_DT) 'year', month(NOTI_REGST_DT) 'month', DAYOFMONTH(NOTI_REGST_DT) 'day',count, subwrtbtText
