Noti sn where o.ORGNZ_NO = sn.ORGNZ_NO and sn.SLCTN_END_DT >= now()) c left outer join organz p on c.pno1 = p.ORGNZ_NO) c2 left outer join organz p2 on c2.pno2 = p2.ORGNZ_NO) c3 left outer join organz p3 on c3.pno3 = p3.ORGNZ_NO )sn LEFT JOIN apndng_file af on sn.SLCTN_NOTI_NO = af.PRNTS_NO)bo where (bo.SLCTN_NOTI_DSTNCT = #{type })
or (bo.SLCTN_NOTI_DSTNCT in ('대학원', '연구실') and bo.SLCTN_NOTI_NO in
#{intMajorList}
) order by bo.NOTI_REGST_DT desc;
order by bo.NOTI_REGST_DT desc;
